var express = require('express');
var bodyParser = require('body-parser');
var materiaDAO = require('dao/materiaDAO');

var router = express.Router();
router.use(bodyParser.json());

router.route('/')
	.get(function (req, res, next) {
		materiaDAO.buscarTodos(function(listaMateria){			
 			res.setHeader('Content-Type', 'application/json');
      		res.write(JSON.stringify(listaMateria));
      		res.end();
      	});
	})
	.post(function (req, res, next) {

		var nombre = req.body.nombre;
		//console.log(req.body);
		console.log('NOmbre:'+nombre);
		if (nombre){
		materiaDAO.buscarNombre(
			nombre,
			function(data){				
				if (data==null)	{
						materiaDAO.insertar(
							req.body,
							function(data){
								res.setHeader('Content-Type', 'application/json');
				      			res.write(JSON.stringify(data));
				      			res.end();
							}
						);	

}
				else {
				res.status(400).send('Ya existe ese codigo');}
			}
		)};		


		
	})
	.put(function (req, res, next) {
		materiaDAO.actualizar(
			req.body,
			function(data){
				res.setHeader('Content-Type', 'application/json');
      			res.write(JSON.stringify(data));
      			res.end();
			}
		);
	});

// Setup the item routes
router.route('/:id')
	.get(function (req, res, next) {
	var id = req.params['id'];
	if (id){
		materiaDAO.buscar(
			id,
			function(data){
				
				res.setHeader('Content-Type', 'application/json');
      			res.write(JSON.stringify(data));
      			res.end();
			}
		);
	}
	else {
		res.send(404, { status: 'Not found' });
	}
}).delete(function (req, res, next) {

		var id = req.params['id'];		
		console.log(id);
		
		materiaDAO.borrar(
			id,
			function(id){
				res.send(204);
			}
		);
	})	
.all(function (req, res, next) {  /// para todos los dem√°s no implementado
	res.send(501, { status: 'Not implemented' });
});

module.exports = router;